package loiane;

public class SequenciaEscape {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//"Hello, World!"
		System.out.print("\" Hello, World\"\n\r"); // /n muda alinha /r leva o cursor para o inicio da linha
		
		
		System.out.print("1\\4"); // Para imprimir  1\4
		
		
		
}

}